import React, { Component } from 'react'
import FacebookLogin from 'react-facebook-login'
import FormInput from './Form'
import Home from './Home'
import './App.css'
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      picture: "",
      token: ""
    };
  } responseFacebook = (response) => {
    if (response.status !== "unknown") {
      this.setState({ name: response.name })
      this.setState({ email: response.email })
      this.setState({ picture: response.picture.data.url })
      this.setState({ token: response.accessToken })
    }
    else {
      console.log("you are not singned in");
    }
  }

  componentDidMount() {
    if (localStorage.getItem('token')) {
      this.setState
        (
          {
            token: localStorage.getItem('token'),
            name: localStorage.getItem('name'),
            email: localStorage.getItem('email'),
            picture: localStorage.getItem('picture')
          }
        )
    }
  }
  componentDidUpdate() {
    if (this.state.token) 
    {
      localStorage('token', this.state.token);
      localStorage('name', this.state.name);
      localStorage('email', this.state.email);
      localStorage('picture', this.state.picture);
    }
  }

  render() {
    return (
      <center>
        <h1>Login</h1>
        <FormInput label="Enter your username" type="text"></FormInput>
        <FormInput label="Enter your password" type="password"></FormInput>       
         <h2>or login using Facebook</h2>        
        <FacebookLogin
          appId="175838644478520"
          autoLoad={false}
          fields="name,email,picture"
          callback={this.responseFacebook} />        
          {
            this.state.token ?
            <Home></Home>
            : null
          }
      </center>
    )
  }
}
