import React, { Component } from 'react'
import FacebookLogin from 'react-facebook-login'
import FormInput from './Form'
import './App.css'
export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "",
      email: "",
      picture: "",
      token: ""
    };
  }   responseFacebook = (response) => {
     if (response.status !== "unknown") {
       this.setState({ name: response.name })
       this.setState({ email: response.email })
       this.setState({ picture: response.picture.data.url })
       this.setState({ token: response.accessToken })
     }
     else {
       console.log("you are not singned in");
     }
  }  
  render() {
    return (
      <center>
        <h1>Login</h1>
        <FormInput label="Enter your username" type="text"></FormInput>
        <FormInput label="Enter your password" type="password"></FormInput>        <h2>or login using Facebook</h2>        <FacebookLogin
          appId="4145685922180371"
          autoLoad={false}
          fields="name,email,picture"
          callback={this.responseFacebook} />        {this.state.token ?
          <div>
            <h3>Welcome {this.state.name}</h3>
            <img src={this.state.picture}></img>
            <h3>The email {this.state.email} is authorized</h3>
          </div>
          : null
        }
      </center>
    )
  }
}
